<?phpglobal $wpdb;$table_setting=$wpdb->prefix . "ccf_Fields";if(isset($_POST['submit'])){	$sql="insert into 			$table_setting (field_name,category_type,field_type) 		values('".$_POST['name']."',		'".$_POST['catType']."',		'".$_POST['type']."'		)";			$wpdb->query($sql);}$is_update = false;if(isset($_GET['mode'])){	if($_GET['mode']=='delete')	{		categoryCustomFields_DB_DeleteCategory($_GET['id']);	}	if($_GET['mode'] == 'edit'){		$is_update=true;	}}?><br/><table><tr>	<td valign="top">			<form method="post">		<table>		<tr>			<td><h3>Add new Field</h3></td>		</tr>		<tr>			<td><label for="name">Field Name</label></td>			<td><input type="text" name="name" style="width:150px"></td>		</tr>		<tr>			<td><label for="type">Field Type</label></td>			<td><select name="type" style="width:150px" <?php if($is_update) echo 'disabled="true"'; ?>>				<option>text</option>				<option>file</option>				<option>image</option>				<option>date</option>			</select>			</td>		</tr>		<tr>			<td><lable for="catType">Category type</label></td>			<td> <select name="catType" style="width:150px" <?php if($is_update) echo 'disabled="true"'; ?>>				<option>Category</option>				<option>Taxonomy</option>				<option>Both</option>			</select></td>		</tr>		<tr>			<td><input type="submit" name="submit" value="Add"/></td>		</tr>		</table>		</form>	</td>		<td width="500px" valign="top" style="padding-left:30px">		<?php		$select="select * from $table_setting";			$cat = $wpdb->get_results($select);		?>		<table class="wp-list-table widefat fixed tags">		<thead>			<tr>				<th class="manage-column column-cb">Name</th>				<th class="manage-column column-cb">Type</th>				<th class="manage-column column-cb">Category Type</th>				<!--<th class="manage-column column-cb">Edit</th>-->				<th class="manage-column column-cb">Delete</th>			</tr>			</thead>			<?php foreach($cat as $c){ ?>				<tr>					<td><?php echo $c->field_name; ?></td>					<td><?php echo $c->field_type; ?></td>					<td><?php echo $c->category_type; ?></td>					<!--<td><a href="options-general.php?page=categoryCustomFields_SettingsPage&mode=edit&id=<?php echo $c->field_name; ?>">Edit</a></td>-->					<td><a href="options-general.php?page=categoryCustomFields_SettingsPage&mode=delete&id=<?php echo $c->field_name; ?>">Delete</a></td>				</tr>			<?php } ?>		</table>	</td></tr></table>